我们可以这样实现_for及_if函数：

var _for = function (begin, end, body) {
    for (var i = begin; i &lt; end; i++) {
        body(i);
    }
};

var _if = function (condition, bodyPart, elsePart) {
    if (condition) {
        bodyPart();
    } else {
        elsePart();
    }
};


